    private void jToggleButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jToggleButton1ActionPerformed
        listNuevas.add(tfAdd.getText());
        tfAdd.setText(null);
        tfAdd.requestFocus();
    }//GEN-LAST:event_jToggleButton1ActionPerformed

    private void botonVaciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonVaciarActionPerformed
        listGenerado.removeAll();
        listNuevas.removeAll();
    }//GEN-LAST:event_botonVaciarActionPerformed

    private void botonBorrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonBorrarActionPerformed
        try{
            listNuevas.remove(listNuevas.getSelectedIndex());
        }catch(ArrayIndexOutOfBoundsException e){}
    }//GEN-LAST:event_botonBorrarActionPerformed

    private void botonGenerarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonGenerarActionPerformed
        int i, n;
        for(i=0; i<listNuevas.getItemCount(); i++){
	n=(int)(Math.random()*10+1);
	switch (n){
            case 1:
            case 2:
            case 3:
            case 4:
            case 5: listGenerado.add(listNuevas.getItem(i)+" - 1");break;
            case 6:
            case 7:
            case 8: listGenerado.add(listNuevas.getItem(i)+" - X");break;
            case 9:
            case 10: listGenerado.add(listNuevas.getItem(i)+" - 2");break;
            }
	}
    }//GEN-LAST:event_botonGenerarActionPerformed

    private void tfAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tfAddActionPerformed
        listNuevas.add(tfAdd.getText());
        tfAdd.setText(null);
        tfAdd.requestFocus();
    }//GEN-LAST:event_tfAddActionPerformed

